word_list = ["bureaueigenaar", "aaien", "aandrijfkracht", "mentaliteitsprobleem", "pyrotechnisch", "query", "raadsvoorstel", "raakpunt","zwoelheid","doodsbang", "beeldvulling", "geluiden","geloofspunt","concept"]
def get_word(word_list):
    word = random.choice(word_list)
    return word.upper()
def play(word):
    word_completion = "_" * len(word)
    guessed = False
    guessed_letters = []
    guessed_words = []
    tries = 6
    print("Welcome to Galgje")
    print(display_hangman(tries))
    print(word_completion)
    print("\n")
    while not guessed and tries > 0:
        guess = input("Raad een letter of woord: ").upper()
        if len(guess) == 1 and guess.isalpha():
            if guess in guessed_letters:
                print("Je hebt dit al geprobeerd: ", guess, "!")
            elif guess not in word:
                print(guess, "is niet in het woord :(")
                tries -= 1
                guessed_letters.append(guess)
            else:
                print("Goed zo,", guess, "is in het woord!")
                guessed_letters.append(guess)
                word_as_list = list(word_completion)
                indices = [i for i, letter in enumerate(word) if letter == guess]
                for index in indices:
                    word_as_list[index] = guess
                word_completion = "".join(word_as_list)
                if "_" not in word_completion:
                    guessed = True
        elif len(guess) == len(word) and guess.isalpha():
            if guess in guessed_words:
                print("Je hebt dit al geprobeerd: ", guess, "!")
            elif guess != word:
                print(guess, " is niet het woord :(")
                tries -= 1
                guessed_words.append(guess)
            else:
                guessed = True
                word_completion = word
        else:
            print("ongeldige input")
        print(display_hangman(tries))
        print(word_completion)
        print("\n")
    if guessed:
        print("Goed geraden!")
    else:
        print("je hebt geen gokken meer" + word + "")


